import React, { useState } from "react";
import "./ToggleStyles.css";

// State là các thông tin lưu trong một component
// Hook là một tính năng mới trong react cho phép chúng ta sử dụng state và các tính năng khác của react mà không cần phải sử dụng class
// Stateless functional component: là component nhưng không sử dụng state
// function Toggle() {
//   return <div className="toggle"></div>;
// }
// Statefull function component: là component có sử dụng state
// function Toggle1() {
// const [count, setCount] = useState();
//   return <div className="toggle"></div>;
// }
function Toggle() {
  // useState nhận vào 1 mảng gồm có 2 tham số: Giá trị khởi tạo và hàm cập nhật giá trị khởi tạo đó
  // VD: const [on, setOn] = useState(false); => false là giá trị khởi tạo được gán cho on
  // Để sử dụng useState, ta cần thực hiện 4 bước
  // Enabling state: useState(initialize value)
  // Initialize value: useState(boolean(true, flase), number(1,2,3,...), string("Tanas"), undefined, null,[1,2,3,4,5],
  // {title: Frontend Developer})
  // Reading state:
  // Update state:

  const array = useState(false);
  console.log(array); // [false, function]
  console.log(array[0]);
  console.log(array[1]);
  const [a, b] = [0, 1];
  console.log(a, b);

  const [on, setOn] = useState(false);
  //   console.log(on, setOn)
  // re-render khi useState() thay đổi (state changes), tức là hàm set giá trị trong useState() được gán giá trị mới thì toàn bộ component sẽ load lại
  console.log(on);

  return (
    <div>
      <div className="toggle">
        <div className="spinner"></div>
      </div>
      <div className="toggle-control">
        <div className="toggle-on" onClick={() => setOn(true)}>
          On
        </div>
        <div className="toggle-off" onClick={() => setOn(false)}>
          Off
        </div>
      </div>
    </div>
  );
}

export default Toggle;
